/**
 * Clear Conditions Macro
 * Remove all status effects/conditions from selected tokens
 */

const tokens = canvas.tokens.controlled;

if (tokens.length === 0) {
  ui.notifications.warn('Please select at least one token.');
  return;
}

console.log(`🧹 Clearing conditions for ${tokens.length} token(s)...`);

let clearedCount = 0;

for (const token of tokens) {
  try {
    const effects = token.actor?.effects || [];

    if (effects.size > 0) {
      // Remove all temporary effects
      const effectsToRemove = effects.filter(effect => !effect.disabled);

      if (effectsToRemove.length > 0) {
        await token.actor.deleteEmbeddedDocuments('ActiveEffect', effectsToRemove.map(e => e.id));
        console.log(`✅ Cleared ${effectsToRemove.length} condition(s) from ${token.name}`);
        clearedCount++;
      } else {
        console.log(`ℹ️ No active conditions on ${token.name}`);
      }
    } else {
      console.log(`ℹ️ No conditions on ${token.name}`);
    }

  } catch (error) {
    console.error(`❌ Failed to clear conditions for ${token.name}:`, error);
  }
}

if (clearedCount > 0) {
  ui.notifications.info(`Cleared conditions from ${clearedCount} token(s)!`);
} else {
  ui.notifications.info('No conditions to clear.');
}
