/**
 * Export Custom Traits Macro
 * Exports only custom/world traits (excludes system built-ins)
 */

console.log('üîÑ Starting custom trait export...');

// Get TraitProvider
let traitProvider = game?.avant?.initializationManager?.getService('traitProvider');

if (!traitProvider) {
  ui.notifications.error('TraitProvider not found. Please ensure the system is loaded.');
  return;
}

try {
  // Get all traits and filter for custom ones
  const allTraits = await traitProvider.getAllTraits();
  const customTraits = allTraits.filter(trait => 
    trait.source !== 'system' && 
    !trait.localKey?.startsWith('AVANT.Trait.')
  );

  if (customTraits.length === 0) {
    ui.notifications.warn('No custom traits found to export.');
    return;
  }

  // Create export data
  const exportData = {
    version: '1.0.0',
    system: 'avant',
    type: 'traits',
    timestamp: new Date().toISOString(),
    traits: customTraits
  };

  // Download as JSON
  const blob = new Blob([JSON.stringify(exportData, null, 2)], { type: 'application/json' });
  const url = URL.createObjectURL(blob);
  const a = document.createElement('a');
  a.href = url;
  a.download = `custom-traits-${new Date().toISOString().split('T')[0]}.json`;
  a.click();
  URL.revokeObjectURL(url);

  console.log(`‚úÖ Export complete: ${customTraits.length} custom traits`);
  ui.notifications.info(`Successfully exported ${customTraits.length} custom traits!`);

} catch (error) {
  console.error('‚ùå Export failed:', error);
  ui.notifications.error(`Export failed: ${error.message}`);
}
