/**
 * Roll Initiative Macro
 * Quickly roll initiative for selected tokens
 */

const tokens = canvas.tokens.controlled;

if (tokens.length === 0) {
  ui.notifications.warn('Please select at least one token.');
  return;
}

console.log(`üé≤ Rolling initiative for ${tokens.length} token(s)...`);

for (const token of tokens) {
  if (token.actor) {
    try {
      // Roll initiative for the actor
      await token.actor.rollInitiative();
      console.log(`‚úÖ Initiative rolled for ${token.actor.name}`);
    } catch (error) {
      console.error(`‚ùå Failed to roll initiative for ${token.actor.name}:`, error);
      ui.notifications.error(`Failed to roll initiative for ${token.actor.name}`);
    }
  }
}

ui.notifications.info(`Initiative rolled for ${tokens.length} token(s)!`);
