/**
 * Toggle Visibility Macro
 * Toggle visibility of selected tokens
 */

const tokens = canvas.tokens.controlled;

if (tokens.length === 0) {
  ui.notifications.warn('Please select at least one token.');
  return;
}

console.log(`üëÅÔ∏è Toggling visibility for ${tokens.length} token(s)...`);

for (const token of tokens) {
  try {
    const isHidden = token.document.hidden;
    await token.document.update({ hidden: !isHidden });

    console.log(`‚úÖ ${token.name}: ${isHidden ? 'visible' : 'hidden'}`);
  } catch (error) {
    console.error(`‚ùå Failed to toggle visibility for ${token.name}:`, error);
  }
}

ui.notifications.info(`Toggled visibility for ${tokens.length} token(s)!`);
